/**
 * More or less a copy of `json-schema-diff-validator`
 * https://bitbucket.org/atlassian/json-schema-diff-validator
 */
import { Operation } from 'fast-json-patch';
declare type SchemaType = {
    [key: string]: any;
};
declare type ValidatorOptions = {
    allowNewOneOf?: boolean;
    allowNewEnumValue?: boolean;
    allowReorder?: boolean;
    allowNewId?: boolean;
};
declare function schemaDiff(originalSchema: SchemaType, changedSchema: SchemaType, options?: ValidatorOptions): Array<Operation>;
declare function schemasAreCompatible(original: SchemaType, changed: SchemaType): boolean;
export { schemaDiff, schemasAreCompatible };
